float plot(vec2 uv, float pct){
  return step( pct-0.01, uv.y) - step(pct+0.01, uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy/iResolution.xy;

    vec3 color = vec3(0.0);

    float pct = plot(uv - vec2(0.5),sin(uv.x * 20.0 + iTime)/10.0);
    color = color+pct*vec3(0.0,1.0,0.0);

    fragColor = vec4(color,1.0);
}
