void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float time = iTime * 0.3;

    float wave = sin(uv.x * 1.0 + time) 
               + sin(uv.y * 10.0 + time)
               + sin((uv.x + uv.y) * 37.0 + time)
               + sin(sqrt(length(uv - 0.5)) * 123.0 + time);

    wave = wave * 1.25;

    vec3 color = vec3(cos(time + wave), sin(time - wave), cos(time));

    fragColor = vec4(color * 10.5 + 1.5, 0.1);
}
