#ifdef GL_ES
precision mediump float;
#endif

#define SE 1532.0
#define PI 3.14159265358979323846
#define u_time iTime
#define u_resolution iResolution

float random(in float x){
    return clamp(fract(sin((x+14.124)*10000.0)),0.01,1.0);
}

float random_sign(in float x){
    return sign(random(x)-0.5);
}

float pattern(in vec2 st, in float xf, in float xm, in float ym){
    vec2 _st = st;
    float rx = floor(_st.x*xf);

    st.x = fract(st.x*xf);
    st.x = step(xm, st.x);
    st.y = sin(random_sign(rx)*u_time*random(rx)*random(rx+xf)*100.0+st.y*180.0*random(rx))
          *sin(st.y*random(rx+10.0)*0.2+u_time*random(rx+10.0)*0.2)
          *sin(st.y*random(rx+30.0)*4.2+u_time*random(rx+30.0)*4.2)
          +0.1*sin(st.y*random(rx+10.0)*11.2+u_time*random(rx+10.0)*0.2);
    st.y = st.y;
    st.y = step(ym, st.y);
    return clamp(st.x+st.y,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = gl_FragCoord.xy/u_resolution.xy;

    //dec
    float xf = 45.0;
    float m = 0.868;
    float n = -0.116;
    //float n = u_mouse.x/u_resolution.x-.5;

    vec2 offset = vec2(0.004*(random(fract(u_time))),0.004);

	vec3 color = vec3(pattern(st+offset, xf, m, n),
                      pattern(st	   , xf, m, n),
                      pattern(st-offset, xf, m, n));

    fragColor = vec4(color,1.0);
}
