#define lineWidth 0.01
#define PI 3.14159265359

vec3 lineColor(float v) {
    return vec3(v, 0.0, 0.0) + v*v;
}

vec3 genLine(float base, float p) {
    float f = abs(base - p);
    float v = lineWidth / (abs(f) + lineWidth);
    return clamp(lineColor(v), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float progress = fract(iTime*0.5);

    vec3 col = genLine(uv.x, progress);

    vec4 oColor = texture(iChannel0, uv);
    float a = col.r / oColor.r;
    fragColor = mix(oColor, vec4(col, 0.0), a);
}
