#define pi 3.141593
float fFractalIterations = 35.0; //fractal parameter 2, range 1 to 128, classic is 32
float fCoordMultiplier; // fractal parameter 3, range 1.0 to 1.5, classic is 1.3
float fCoordPreMulti; // fractal parameter 4, classic is 2.0
float fRotCenter; // fractal parameter 5, classic is -1.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
fFractalIterations = 25.0 + sin(iTime * 0.45) * 12.0;
fCoordMultiplier = 1.2 + cos (1.0 + iTime * 0.3) * 0.01;
fCoordPreMulti = 1.0 + sin(iTime * 0.92) * 0.3 * cos(1.32 + iTime * 0.62); //v2 =  1.0
fRotCenter = -1.5 + sin(iTime * 0.92); //v2 = -1.5
float fRotAngle = pi / 4.0 + 1.4 * sin(iTime * 0.1) * (pi / 4.0 - 1.2);//pi / 2.0 - sin(iTime * 0.9) * 0.2 - 1.0; //fractal parameter 1
    vec2 normalizedCoord = fragCoord / iResolution.xy * fCoordPreMulti + fRotCenter;
    // coordinates from center, range -1 to 1,
    // set floats to 2.0 and -1.0 for classic
    //start of fractal loop
    for (float i = 0.0; i < fFractalIterations; i += 1.0)
    {
    normalizedCoord = abs(normalizedCoord) - 0.6;
    //makes 4
    normalizedCoord *= fCoordMultiplier;
    normalizedCoord *= mat2(cos(fRotAngle * 1.02) * 0.9, -sin(fRotAngle) * 1.2,
sin(fRotAngle) * 1.14, cos(fRotAngle * 1.1)); // rotation
    }//end of fractal loop
    fragColor = vec4(
    abs(sin(iTime * 0.914 * sin(iTime * 0.3))) * length(normalizedCoord),// + vec2(0.2, -0.2)), //RED 
    abs(cos(iTime * 1.254 * sin(iTime * 0.119))) * length(normalizedCoord), //+ vec2(0.0, -0.0)), //GREEN
    abs(sin(iTime * 1.616)) * length(normalizedCoord), //+ vec2(0.3, -0.0)), //BLUE
    1.0); //ALPHA
}
