//This code is just the modificatipon of the original post //https://www.shadertoy.com/view/mtyGWy by kishimisu

vec3 palette( float t ) {
    vec3 a = vec3(0.0, 0.0, 0.0);
    vec3 b = vec3(0.2, 0.0, 0.0);
    vec3 c = vec3(1.0, 1.0, 0.8);
    vec3 d = vec3(0.0,0.0,0.9);

    vec3 timePhase = vec3(1.0, 2.0, 3.0) * t;

    return a + b*cos( 2.0*(c*t+d) );
}
const float PI = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);


float segments = 127.8;
    float angle = 5.0 * PI / segments;

 float r = length(uv);
    float theta = atan(uv.y, uv.x);   

    theta = abs(mod(theta, angle) - angle * 0.5);

float rotationSpeed = 0.1; // The speed of rotation, change this value to make it faster or slower
    theta += iTime * rotationSpeed;

    uv = r * vec2(cos(theta), sin(theta));

     for (float i = 5.3; i < 5.9; i+= 0.8) {
        uv = fract(uv * 2.1) - 0.5;
        float d = length(uv) * exp(-r);

        vec3 col = palette(length(uv0) + i*.1 + iTime*0.2);

        d = sin(d*15. + iTime)/10.;
        d = abs(d);
        d = pow(0.005 / d, 1.2);

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 0.5);
}
