float rectSDF(vec2 st, vec2 s) {
    st = st * 2.0 - 1.0;
    return max(abs(st.x / s.x), abs(st.y / s.y));
}

float fill(float x, float size) {
    return 1.0 - step(size, x);
}
float fillAA(float x, float size) {
    return 1.0 - smoothstep(size - 0.011, size, x);
}

vec2 rotate(vec2 uv, float angle) {
    uv = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * (uv - 0.5);
    return uv + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.776, 0.851, 0.878);

    float MX = iMouse.x / iResolution.x;
    float MY = iMouse.y / iResolution.y;

    vec2 bgS = vec2(0.5, 1.0);

    vec2 bgOffset = vec2(0.25, 0.0);

    float bgLeft = fill(rectSDF(uv + bgOffset, bgS), 1.0);
    float bgRight = fill(rectSDF(uv - bgOffset, bgS), 1.0);
    float mid = fill(rectSDF(uv, bgS), 1.0);
    float midRight = fill(rectSDF(uv - vec2(0.2, 0.0), vec2(0.1, 1.0)), 1.0);

    float B = fillAA(rectSDF(rotate(uv, -0.03), vec2(0.72)), 1.0);

    vec3 bgLeftColor = vec3(0.718, 0.388, 0.624);
    vec3 bgRightColor = vec3(0.263, 0.169, 0.357);
    vec3 midColor = vec3(0.918, 0.325, 0.282);
    vec3 midRightColor = vec3(0.149, 0.231, 0.282);
    vec3 BColor = vec3(0.569, 0.302, 0.533);

    color = bgLeft * bgLeftColor + bgRight * bgRightColor;
    color = mix(color, B * BColor, B);
    color = mix(color, mid * midColor, mid);
    color = mix(color, midRight * midRightColor, midRight);

    // color = mix(color, bgLeft * bgLeftColor, MX);
    // color = mix(color, bgRight * bgRightColor, MX);
    color = mix(color, B * BColor, B * MX);

    fragColor = vec4(color, 1.0);
}
