void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;
    float fade = 0.02;
    float thinness = 0.02;
    vec3 circleColor = vec3(0.7, 0.2, 0.5);
    float dist = 1.0 - sqrt(dot(uv, uv));
    vec3 color  =  vec3(smoothstep(0.0, fade, dist));
         color *= vec3(smoothstep(fade + thinness, thinness, dist));

    // Output to screen
    fragColor = vec4(color, 1.0);
    fragColor.rgb *= circleColor;
}
