// https://twitter.com/kamoshika_vrc/status/1656260144840478720?s=20

mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

float fsnoise(vec2 p) {   
    p *= 2210.52;
    return sin(p.x)*.4 + cos(p.y)*.9;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.14, t = iTime * .1;

    vec4 o;

    float i,c=33./PI, d=c,g,dpoint;
    vec3 oPoint = normalize(vec3(fragCoord.xy-iResolution.xy*.5,-iResolution.y));

    for(i=0.;i++< 38. && d > 0.1; g += d){  
        vec3 point = oPoint * rotate3D(.9,vec3(fragCoord.xy,fragCoord.y))*g;
        point.yz += 222.;
        vec2 I = ceil(point.xz = vec2(log( d = length(point.xz))-t , atan(point.z,point.x)) * c);
        dpoint = (.4-abs(fract((fsnoise(I)<.5? -point.z : point.z)-point.x)-.6)) * .7;
        d = max(point.y, dpoint * d/c);
    }

    o+=9./i;
    o.r -= g*.0003;
    o.b += -dpoint*5.;

    // Output to screen
    fragColor = o;
}
