void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 col = 1.0 - fragCoord/iResolution.xy;

    vec2 col2 = vec2(uv);

    col += sin(iTime);
    col2 += 1.0 - sin(iTime);

    float col3 = sin(iTime * 4.0);

    vec2 combinedColor = (col - col2);

    fragColor = vec4(combinedColor, col3, 1.0);
}
