void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float fastBlink = (sin(5.*iTime)+1.)*0.5;
    float slowBlink = (sin(2.*iTime)+1.)*0.5;

    float blend = (sin(0.3*iTime)+1.)*0.5;

    float blink = mix(slowBlink,fastBlink,blend);

    if(BlinkMask(blink, uv)>0.5)
    //if(BlinkMask(slowBlink, uv)>0.5)
    {
        col = vec3(0.0);
    }
    col*=pow(1.-blink,0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float BlinkMask(float blink,vec2 uv)
{
    //shape by down half
    vec2 pos = uv;
    if(uv.y<0.5f)
    {
        pos.y*=2.0;
    }
    else
    {
        pos.y = 1.0 - uv.y;
        pos.y*=2.0;
    }


    float x = pos.x-0.5;
    float eyeline = x*x-0.5;
    eyeline = mix(eyeline,blink,blink);
    if(pos.y<eyeline)
    {
        return 1.0;
    }
    else
    {
        return 0.0;
    }
}
