precision mediump float;
#define ADD_BAR
#define ADD_NOISE

struct ColorSet { vec3 col1, col2; };
const ColorSet Pattern1 = ColorSet(vec3(1.0, 1.0, 1.0), vec3(0.0, 0.0, 0.0));
const ColorSet Pattern2 = ColorSet(vec3(0.3882, 1.0, 0.9686), vec3(0.7333, 0.949, 0.502));
const ColorSet Pattern3 = ColorSet(vec3(0.9176, 0.3804, 0.9451), vec3(0.5373, 1.0, 0.9059));
const ColorSet Pattern4 = ColorSet(vec3(0.9843, 0.9608, 0.6941), vec3(1.0, 0.3686, 0.3686));
const ColorSet Pattern5 = ColorSet(vec3(1.00, 1.00, 1.00), vec3(0.00, 0.00, 1.00));

#define COLOR_PATTERN_NUM 5
ColorSet[COLOR_PATTERN_NUM]list = ColorSet[COLOR_PATTERN_NUM](
    Pattern1,
    Pattern2,
    Pattern3,
    Pattern4,
    Pattern5
);

#define GRAPHIC_PATTERN_NUM 4
float GraphicA(vec2 uv) {
    float n = uv.x;
    float animSpeed = 0.2;
    float a = 0.5 + 0.5 * sin(iTime * animSpeed);
    n += floor(uv.y / 0.1) * a;
    return mod(n, 1.0);
}
float GraphicB(vec2 uv) {
    float n = uv.x;
    float animSpeed = 0.3;
    float a = iTime * animSpeed;
    float size = 0.1;
    n += floor(uv.y / size) * size;
    n += floor(uv.y / (size * 0.5)) * 0.3 - a;
    return mod(n, 1.0);
}
float GraphicC(vec2 uv) {
    float n = uv.x;
    float stepH = 0.1;
    float stepW = 0.3;
    float s = abs(mod(uv.y, 1.0) - 0.5);
    float animSpeed = 1.0;
    float a = (0.5 + 0.5 * sin(iTime * animSpeed + floor(s / stepH) * stepW));
    n += a;
    return mod(n, 1.0);
}
float GraphicD(vec2 uv) {
    float n = uv.x;
    float stepH = 0.1;
    float stepW = 0.3;
    float animSpeed = 0.3;
    float a = noise(vec2(0, iTime * animSpeed + floor(uv.y / stepH) * stepW));
    n += a;
    return mod(n, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 color = vec3(0, 0, 0);
    vec2 uv = fragCoord / min(iResolution.x, iResolution.y);

    ///--- SHAPE & ANIMATION
    int gIndex = int(iTime / 5.0)% GRAPHIC_PATTERN_NUM;
    float n = gIndex < 1 ? GraphicA(uv)
    : gIndex < 2 ? GraphicB(uv)
    : gIndex < 3 ? GraphicC(uv)
    : GraphicD(uv);

    ///--- COLOR
    int colIndex = int(iTime / 3.0)% COLOR_PATTERN_NUM;
    vec3 col1 = list[colIndex].col1;
    vec3 col2 = list[colIndex].col2;
    color = oklab_mix(col1, col2, n);

    ///--- ADD NOISE
    #ifdef ADD_NOISE
    color += random(uv) * 0.1;
    #endif

    ///--- ADD GRADATION BAR on LEFT
    #ifdef ADD_BAR
    vec3 gradation = oklab_mix(col1, col2, uv.y);
    float barWidth = 0.05; // 25.0 / iResolution.x;
    float lineWidth = 0.002; //2.0 / iResolution.x;
    color = uv.x < barWidth ? gradation :
    uv.x < (barWidth + lineWidth) ? vec3(0, 0, 0) : color;
    #endif

    fragColor = vec4(color, 1.0);
}

/*--------------------------------------------------
 # glslUtils
 - https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
 - https://github.com/ashima/webgl-noise
 - https://thebookofshaders.com/
 - 
--------------------------------------------------*/

float random(float n){return fract(sin(n) * 43758.5453123);}
float random(vec2 st) {return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);}
float noise(float p){
	float fl = floor(p), fc = fract(p);
	return mix(random(fl), random(fl + 1.0), fc);
}
float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
  vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(random(b), random(b + d.yx), f.x), mix(random(b + d.xy), random(b + d.yy), f.x), f.y);
}


float mod289(float x) { return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0;}

float permute(float x) { return mod289(((x * 34.0) + 1.0) * x);}
vec2 permute(vec2 x) { return mod289(((x * 34.0) + 1.0) * x);}
vec3 permute(vec3 x) { return mod289(((x * 34.0) + 1.0) * x);}
vec4 permute(vec4 x) { return mod289(((x * 34.0) + 1.0) * x);}

/*============================== SimplexNoise ==============================*/
//### 2D
float snoise(vec2 v) {
  vec4 C   = vec4(0.211324865405187, 0.366025403784439, -0.577350269189626, 0.024390243902439);
  vec2 i   = floor(v + dot(v, C.yy));
  vec2 x0  = v - i + dot(i, C.xx);
  vec2 i1  = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i      = mod289(i);  // Avoid truncation effects in permutation
  vec3 p = permute(permute(i.y + vec3(0.0, i1.y, 1.0)) + i.x + vec3(0.0, i1.x, 1.0));

  vec3 m  = max(0.5 - vec3(dot(x0, x0), dot(x12.xy, x12.xy), dot(x12.zw, x12.zw)), 0.0);
  m       = m * m;
  m       = m * m;
  vec3 x  = 2.0 * fract(p * C.www) - 1.0;
  vec3 h  = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * (a0 * a0 + h * h);
  vec3 g;
  g.x  = a0.x * x0.x + h.x * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
float fbm(vec2 uv, int oct) {
  vec2  pos = uv;
  float amp = 1.0, val = 0.0;
  for (int i = 0; i < oct; i++) {
    val += amp * snoise(pos);
    pos *= 2.0;
    amp *= 0.5;
  }
  return val;
}

//### 3D
float snoise(vec3 v) {
  vec2 C   = vec2(1.0 / 6.0, 1.0 / 3.0);
  vec4 D   = vec4(0.0, 0.5, 1.0, 2.0);
  vec3 i   = floor(v + dot(v, C.yyy));
  vec3 x0  = v - i + dot(i, C.xxx);
  vec3 g   = step(x0.yzx, x0.xyz);
  vec3 l   = 1.0 - g;
  vec3 i1  = min(g.xyz, l.zxy),i2  = max(g.xyz, l.zxy);
  vec3 x1  = x0 - i1 + C.xxx, x2  = x0 - i2 + C.yyy, x3  = x0 - D.yyy;
  i        = mod289(i);
  vec4  p  = permute(permute(permute(i.z + vec4(0.0, i1.z, i2.z, 1.0)) + i.y + vec4(0.0, i1.y, i2.y, 1.0)) + i.x + vec4(0.0, i1.x, i2.x, 1.0));
  float n_ = 0.142857142857;
  vec3  ns = n_ * D.wyz - D.xzx;
  vec4  j  = p - 49.0 * floor(p * ns.z * ns.z);
  vec4  x_ = floor(j * ns.z);
  vec4  y_ = floor(j - 7.0 * x_);
  vec4  x  = x_ * ns.x + ns.yyyy;
  vec4  y  = y_ * ns.x + ns.yyyy;
  vec4  h  = 1.0 - abs(x) - abs(y);
  vec4  b0 = vec4( x.xy, y.xy );
  vec4  b1 = vec4( x.zw, y.zw );
  vec4  s0 = floor(b0) * 2.0 + 1.0, s1 = floor(b1) * 2.0 + 1.0;
  vec4  sh = -step(h, vec4(0, 0, 0, 0));
  vec4  a0 = b0.xzyw + s0.xzyw * sh.xxyy, a1 = b1.xzyw + s1.xzyw * sh.zzww;
  vec3  p0 = vec3(a0.xy, h.x), p1 = vec3(a0.zw, h.y), p2 = vec3(a1.xy, h.z), p3 = vec3(a1.zw, h.w);
  //Normalise gradients
  vec4 norm = 1.79284291400159 - 0.85373472095314 * vec4(dot(p0, p0), dot(p1, p1), dot(p2, p2), dot(p3, p3));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;
  // Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0, x0), dot(x1, x1), dot(x2, x2), dot(x3, x3)), 0.0);
  m      = m * m;
  return 42.0 * dot(m * m, vec4(dot(p0, x0), dot(p1, x1), dot(p2, x2), dot(p3, x3)));
}
float fbm(vec2 uv, int oct, float time) {
  vec2  pos = uv;
  float amp = 1.0;
  float val = 0.0;
  for (int i = 0; i < oct; i++) {
    val += amp * snoise(vec3(pos, time));
    pos *= 2.0;
    amp *= 0.5;
  }
  return val;
}



//-----
vec3 oklab_mix( vec3 colA, vec3 colB, float h )
{
    // https://bottosson.github.io/posts/oklab
    const mat3 kCONEtoLMS = mat3(                
         0.4121656120,  0.2118591070,  0.0883097947,
         0.5362752080,  0.6807189584,  0.2818474174,
         0.0514575653,  0.1074065790,  0.6302613616);
    const mat3 kLMStoCONE = mat3(
         4.0767245293, -1.2681437731, -0.0041119885,
        -3.3072168827,  2.6093323231, -0.7034763098,
         0.2307590544, -0.3411344290,  1.7068625689);

    // rgb to cone (arg of pow can't be negative)
    vec3 lmsA = pow( kCONEtoLMS*colA, vec3(1.0/3.0) );
    vec3 lmsB = pow( kCONEtoLMS*colB, vec3(1.0/3.0) );
    // lerp
    vec3 lms = mix( lmsA, lmsB, h );
    // gain in the middle (no oaklab anymore, but looks better?)
 // lms *= 1.0+0.2*h*(1.0-h);
    // cone to rgb
    return kLMStoCONE*(lms*lms*lms);
}
