mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,0,1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*5.;
    p.z-=8.;
    p*=rotate3D(1.8,q);
    p.xz*=rotate2D(iTime);

    for(j=0.;j++<4.;)
        p=abs(abs(reflect(p,q)/2.7)-.05)-.005,
        d=max(min(p.z,p.x),p.y)-.005,
        d-=dot(p,q)/2e4;
    e+=d;
}o+=8./i;o.b+=5./i;
}
