#define pi 3.14159265359
#define pi2 1.57079632679

#define FUNC sin

float lerp(float a, float b, float cf)
{
    return a + (b - a) * cf;
}

float remap(float x, float a, float b, float A, float B)
{
    float cf = (x - a) / (b - a);
    return lerp(A, B, cf);
}

float shapecf(float r, float thresh, float tolerance, float from, float to, float x, float y, float direction)
{    
    float ang = atan(y, x) + mod(iTime, pi * 2.0) * direction;

    float linex = remap(ang, -pi2, pi2, from, to);
    float liney = FUNC(linex);

    float R = remap(liney, -thresh, thresh, 0.0, 2.0 * r);    
    float k = y / x;

    float X = sqrt(R * R / (1.0 + k * k)) * sign(x);
    float Y = k * X;

    float len = length(vec2(x, y) - vec2(X, Y));    
    float cf = 1.0 - len / tolerance;

    return clamp(cf, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.0;
    uv -= 1.0;
    uv *= pi * 2.2;
    uv.x *= iResolution.x / iResolution.y;

    float a = sin(iTime);
    float b = abs(a) + 0.1;
    float c = sign(a);

    float f = shapecf(5.0, b * 40.0, 0.2, -pi * 12.0, pi * 12.0, uv.x, uv.y, 1.0 * c);
    f = smoothstep(0.3, 0.8, f);

    float g = shapecf(2.5, b * 40.0, 0.2, -pi * 12.0, pi * 12.0, uv.x, uv.y, -1.0 * c);
    g = smoothstep(0.3, 0.8, g);

    vec3 clr = vec3(0.9, 0.8, 0.2) * max(f, g);    
    fragColor = vec4(clr, 1.0);
}
