mat2 r(float a){ return mat2( cos(a), sin(a)*.9, -sin(a)*.9, cos(a));}

void mainImage( out vec4 O, vec2 u ){    
    vec2 R = iResolution.xy, U = ( u - .5*R ) / R.x;//*.06;

    float d=1., t=0., T=iTime, i=t, shape = 1.7;

	vec3 p = -50.*vec3(.9*sin(T),0,cos(T)), q,
         n = normalize(p),
         X = vec3(n.z,0,-n),
         Y = cross(X,n),         
         D = normalize( U.x*X + U.y*Y - n*3. );

    for(O-=O; i<12. && d>.02 && d<32.; i++){
        q = p;
        for( int i = 0; i++<8;){
            q.yz *= r(shape+sin(T)*.2); //shape
            q.xz *= r(1.); 
            q.xz = abs(q.xz) - .2; //scale
            }
        t += d = dot(sign(q),q)/1.;  
        p += d*D;
        O.rgb += mix(vec3(0.,.6,.9), vec3(1,0,1), length(p)*.03 )/d/64.;        
    }
}
