void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    //Change the intensity of the vignette here (Beware it is used as an exponent)
    float vignette_intensity = 1.1; 

    float vignetteX = 1.0 - atan(abs((uv.x * 2.0) - 1.0));
    float vignetteY = 1.0 - atan(abs((uv.y * 2.0) - 1.0));

    float vignette = pow(vignetteX, vignette_intensity) * pow(vignetteY, vignette_intensity);


    vec4 tex = texture(iChannel0, uv);
    vec4 color = vec4(tex.r * vignette, tex.g * vignette, tex.b * vignette, 1.0);


    fragColor = color;
}
