void circle(out vec4 fragColor,in float r,vec2 center,float ycorner,vec2 uv) {
    float t = mod(iTime/2.0,4.0*(1.0-r+center.x));
    float ty = mod(iTime/2.5,4.0*(ycorner-r+center.y));
    float xc,yc;

    xc=(-1.0+r+center.x)+min(abs(t),(4.0*(1.0-r+center.x))-abs(t));
    yc=-(ycorner-r+center.y)+min(abs(ty),(ycorner-r+center.y)*4.0-abs(ty));

    fragColor.b=(uv.x+1.0)/2.0;

    if(length( uv-vec2(xc,yc)) < r  ) {
        fragColor.rgb=vec3(1.0,0.5,0.5);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.xy - 1.0);

    // Output to screen
    uv *=2.0;
    uv -= 1.0;
    uv.y *= iResolution.y/iResolution.x;

    // Ron's bouncing ball


    float ycorner=iResolution.y/iResolution.x;

    float r=0.1;
    circle(fragColor, r, vec2(-0.5,0.5), ycorner, uv);
    //circle(fragColor, r, vec2(0.0,0.0), ycorner, uv);    
}
