#define R iResolution.xy
#define T (iTime)
#define M_PI (3.1416)
#define TAU (M_PI * 2.0)


mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

float noise(vec2 p) { return fract(cos(p.x*3.2915+p.y)*dot(p, vec2(3.2151, 2.9215))); }

vec3 textureShuffle(sampler2D tex, vec2 uv) {
    vec2 id = floor(uv);
    uv = fract(uv);
    float r = noise(id);
    uv -= 0.5;
    uv *= rot(r*TAU);
    uv += 0.5;
    return texture(tex, uv).rgb;
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = fc/R.xy;

    uv *= 4.0 + (8.0 * (0.5+(0.5*cos(T))));

    float C = iMouse.z > 0.01 ? iMouse.x : R.x*0.5;
    if (fc.x < C) {
        col = texture(iChannel0, uv).rgb;
    } else {
        col = textureShuffle(iChannel0, uv).rgb;
    }

    if (fc.x >= C-4.0 && fc.x <= C+4.0) col *= 0.;

    O = vec4(col, 1.0);
}
