const float speed = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    // textures
    vec3 col_tex1 = texture(iChannel0, uv).rgb;
    vec3 col_tex2 = texture(iChannel1, uv).rgb;

    // time
    float t = sin(iTime*speed)*0.5+0.5;

    // lerp
    vec3 col = mix(col_tex1, col_tex2, step(t, uv.x)); // use floor(t+0.5) for quick change

    // out
    fragColor = vec4(col, 1.0);
}
