float boxSDF(vec2 uv, vec2 p)
{
    float dX = max(abs(uv.x) - p.x, 0.);
    float dY = max(abs(uv.y) - p.y, 0.);
    return sqrt(dX*dX + dY*dY);
}

float innerShadow(float radius, float feather, vec2 uv, vec2 p, float angle, float thickness)
{
    vec2 offset = vec2(sin(angle), cos(angle));
    float A = smoothstep(radius - feather, radius + feather, boxSDF(uv + offset * thickness, p));
    float B = smoothstep(radius + feather, radius - feather, boxSDF(uv - offset * thickness, p)); 
    return A*B;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    vec2 p = vec2(0.3, 0.1);
    float d = boxSDF(uv, p);

    float bg = step(fract(uv.x * 10.0 + floor(uv.y * 20.0) * 0.5), 0.5);
    vec3 col = vec3(bg * 0.05 + 0.3); //background

    float angle = iTime * 2.5;

    col = mix(vec3(0.0, 0.0, 0.0), col, smoothstep(0.0, 0.11, boxSDF(uv + vec2(sin(angle), cos(angle)) * 0.2, p) + 0.04)); //soft shadow alphablend
    col += vec3(0.0, 0.1, 0.5) * smoothstep(0.15, 0.04, d) ; //outerglow with additive blend
    col = mix(vec3(1.0, 0.7, 0.0), col, smoothstep(0.05, 0.054, d)) ; //stroke with alphablend
    col = mix(vec3(0.7, 0.2, 0.0), col, smoothstep(0.009, 0.011, d)) ; //stroke with alphablend
    col= mix(col, vec3(1.0, 1.0, 0.4), innerShadow(0.049, 0.003, uv, p, angle, 0.011)); //inner shadow with alphablend

    vec2 box = step(abs(uv), p);
    col += vec3(box.x * box.y); //box

    fragColor = vec4(col,1.0);
}
