//AA and colors are from iq 

//I adhered to the mathematics and pseudocode outlined in the following book.
//Pickover, C. A. (2000). The pattern book: Fractals, art, and nature. World Scientific. 

#define AA 3
const float PI = 3.14159265;
const int MAX_ITER = 14;
const float ESCAPE_RADIUS_SQ = 123.0;
const float SQUARED_MIN = 15.0;
//The book presents this function in pseudocode =)
vec2 spidermorph(vec2 z, vec2 c) {
    int i;
    vec2 newZ;
    for (i = 0; i < MAX_ITER; i++) {
        vec2 zz = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y);
        vec2 z4 = vec2(zz.x*zz.x - zz.y*zz.y, 2.0*zz.x*zz.y);
        newZ = zz + z4 - z + c;
        float tz = newZ.x;
        float iz = newZ.y;
        if(tz * tz > ESCAPE_RADIUS_SQ || iz * iz > ESCAPE_RADIUS_SQ) break;
        z = newZ;
    }
    if(newZ.x * newZ.x < SQUARED_MIN || newZ.y * newZ.y < SQUARED_MIN) return vec2(float(i), dot(z, z));
    return vec2(0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 col = vec3(0.0);

    vec2 pan = vec2(0.8402, 1.0052); 
    float duration = 20.0; 
    float pauseDuration= 0.0;
    float minZoom= 0.0002;
    float maxZoom=7.5;
    float t = mod(iTime, duration+1.1);
    float normalizedT= 1.0 - t/(duration-pauseDuration); 
    float zoomLevel = minZoom*pow(maxZoom/minZoom,normalizedT);
    zoomLevel = mix(maxZoom, zoomLevel, smoothstep(0.0, pauseDuration, t)); 

    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 uv = ((fragCoord + vec2(float(m), float(n))/float(AA) - 0.5*iResolution.xy)/min(iResolution.y,iResolution.x)
        *zoomLevel+pan)*2.033-vec2(2.04278);
        vec2 z_and_i = spidermorph(uv, vec2(-0.78, 0.00)); 

        float iter_ratio = z_and_i.x / float(MAX_ITER);
        float lenSq = z_and_i.y;

float c1 = pow( clamp(2.00 * sqrt(lenSq), 0.0, 1.0), 1.5 );
vec3 col1 = 0.5 + 0.5 * cos(3.0 + iTime + vec3(0.0, 0.5, 1.0) + PI*vec3(2.0 * iter_ratio));
vec3 col2 = 0.5 + 0.5 * cos(2.1 + iTime + PI * vec3(lenSq));
vec3 col3 = 0.5 + 0.5 * sin(1.0 + PI * vec3(1.5 + 2.5 * lenSq));
col += 4.0*sqrt(c1*col1*col2*col3);
    }

    col /= float(AA*AA);
    fragColor = vec4(col, 1.0);
}
