void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float time = sin(radians(100.0) * 4.0 *(iTime + uv.x))*0.25;
    uv = uv + vec2(0.0, time * 0.05);

    if(uv.y > 0.66) fragColor = vec4(0.1, 0.4, 1.0, 1.0);

    else if(uv.y < 0.33) fragColor = vec4(1.0, 1.0, 1.0, 1.0);

    else fragColor = vec4(0.0, 0.0, 0.0, 0.0);

}
