float remap(float x, vec2 from, vec2 to) {

    return ((x - from.x) * (to.y - to.x) / (from.y - from.x)) + to.x;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    float screenAspect = iResolution.x / iResolution.y;

    float channelAspect = iChannelResolution[0].x / iChannelResolution[0].y;

    float pad = (screenAspect - channelAspect) * 0.5;

    vec2 from = vec2(0., 1.);

    vec3 col = vec3(0.);

    if (pad > 0.) {        

        vec2 to = vec2(-pad, 1. + pad);

        uv.x = remap(uv.x, from, to);

        if (uv.x >= 0. && uv.x <= 1.) {

            col = texture(iChannel0, uv).xyz;

            // col.x = 1.;
        }
    }
    else if (pad < 0.) {

        vec2 to = vec2(pad, 1. - pad);

        uv.y = remap(uv.y, from, to);

        if (uv.y >= 0. && uv.y <= 1.) {

            col = texture(iChannel0, uv).xyz;

            // col.x = 1.;
        }
    }


	fragColor = vec4(col ,1.0);
}
