const vec4 Black = vec4(0,0,0,1.);

float RFunc(float x) {
    return 20.*sin(1./10.*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float y = RFunc(x);
    float level_y = iResolution.y / 2.;
    float distance = length(fragCoord - (vec2(x,y  + level_y)));
    float AllowDeviation = 10.;

    if(distance <= AllowDeviation) {
        fragColor = Black;
    }
}
