void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Time = fract(iTime / 15.) * 15.;

    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv *= 2.;
    uv *= 3.1415936 * (cos(Time * .5) + .1)*2.;

    float r = length(uv);
    float rinit = r;
    r = tanh(r);
    r = r/rinit;
    uv *= r;

    vec2 a = vec2(1.);
    vec2 f = vec2(1.);

    vec4 seed = vec4(sin(Time * .2)*.5 + .5, (sin(Time * .6)*.5 + .5), cos(Time * .1)*.5 + .5, (cos(Time * .4)*.5 + .5));

    float buffer = 0.;
    seed *= 1. - (buffer * 2.);
    seed += buffer;
    seed.yw += 1. + buffer;

    vec3 o = vec3(0.);

    for (float i = 0.; i < (sin(Time * 1.)*.5 + .5)*100.; i++) {
        o += (sin(uv.x * f.x)*a.x) * (sin(uv.y * f.y)*a.y) * vec3(sin(iTime), cos(iTime + 20283.), sin(iTime + 123897.));
        a *= seed.xz;
        f *= seed.yw;
    }

    o += 1.;
    o *= .5;

    fragColor = vec4(o, 1.);
}
