vec3 palette(float t) //http://dev.thi.ng/gradients/
{
    vec3 a = vec3(0.500,0.500,0.500);
    vec3 b = vec3(0.500,0.500,0.500);
    vec3 c = vec3(1.000,1.000,1.000);
    vec3 d = vec3(0.263,0.416,0.557);

    return a+b*cos(6.28318*(c*t+d));
}

float sdStar5(in vec2 p, in float r, in float rf)
{
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 uv0 = uv;
    vec3 col,final_col = vec3(0.0);
    float sine_freq   = 8.0,
          neon_coeff  = 0.01,
          time_coeff  = 0.4,
          scale_fac   = 1.3,
          i_fac       = 0.4;

    for(float i=0.0;i<3.0;i++){
    uv = fract(scale_fac*uv)-0.5;

    float d = length(uv) * exp(-length(-uv0));
    d = sdStar5(uv,length(uv0),5.0);
    col = palette(length(uv0)+ iTime*time_coeff + i*i_fac);
    d = sin(d*sine_freq + iTime)/sine_freq;
    d = abs(d);
    d = pow(neon_coeff/d,1.3); //improves contrast


    final_col += col*d;}

    fragColor = vec4(final_col,1.0);
}
