// just a function to shift the hue of a texture...

#define saturate(v) clamp(v,0.,1.)

vec3 HueShift(vec3 source, float shift)
{
    // convert to HSV
	vec4 K = vec4(0.,-1./3.,2./3.,-1.);
	vec4 p = mix(vec4(source.bg ,K.wz),vec4(source.gb,K.xy ),step(source.b,source.g));
	vec4 q = mix(vec4(p.xyw,source.r ),vec4(source.r ,p.yzx),step(p.x,source.r));
    float d = q.x-min(q.w,q.y);
	float e=1e-10;
	vec3 c = vec3(abs(q.z+(q.w-q.y)/(6.*d+e)),d/(q.x+e),q.x);

    // apply shift
    c.x = fract(c.x+shift);

    // convert to RGB
	K = vec4(1.,2./3.,1./3.,3.);
	return c.z*mix(K.xxx,saturate(abs(fract(c.x+K.xyz)*6.-K.w)-K.x),c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 sourcecol = texture(iChannel0, uv).xyz;

    float shift = fract(iTime);
    sourcecol = HueShift(sourcecol,shift);

    fragColor = vec4(sourcecol,1.0);
}
