vec2 Cpow(vec2 z, float n) {
    vec2 rt = vec2(length(z), atan(z.y, z.x));
    return pow(rt.x, n) * vec2(cos(n*rt.y), sin(n*rt.y));
}

void mainImage( out vec4 o, vec2 uv )
{
    o = vec4(0.);
    float margin = .5 * (iResolution.x - iResolution.y);
	uv = 1.*(uv - vec2(1.,0.)*margin) / iResolution.yy;
    uv = (2. * uv - vec2(1.5, 1.)) * (sin(iTime/5.)*.1+1.);
    vec2 z = vec2(uv);
    for (int i = 0; i < 9; i++) z = Cpow(z, 2.) + uv;
    o.xy = abs(z);
}
