float sdEquilateralTriangle(in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p=vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}
mat2 rotate2d(float angle)
{
	return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}
vec3 splash_green=vec3(0.82,0.9,0.79);
vec3 pale_aqua=vec3(0.76,0.89,0.89);
vec3 blue_fog=vec3(0.57,0.7,0.77);
vec3 delft_blue=vec3(0.08,0.2,0.52);
vec3 wedgwood_blue=vec3(0.08,0.51,0.67);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 col=vec3(1.0);
    float d,d2,d3;
    float mix_col;
    float AA=3.0/min(iResolution.x, iResolution.y);
    for(float x=1.0;x<15.0;x+=3.)
    {
        for(float y=0.0;y<15.0;y++)
        {
            d =sdEquilateralTriangle(p*rotate2d(iTime*y*0.1)+vec2(x,y)*0.3-1.8,0.25);
            d2=sdEquilateralTriangle(p*rotate2d(iTime*y*0.1)+vec2(x-1.,y)*0.3-1.8,0.25);
            d3=sdEquilateralTriangle(p*rotate2d(iTime*y*0.1)+vec2(x+1.,y)*0.3-1.8,0.25);

            col=(d>0.0)   ? col:splash_green;
            col=(d2>0.0)  ? col:pale_aqua;
            col=(d3>0.0)  ? col:wedgwood_blue;
            col=(max(d,d2)>0.0)?col:delft_blue;
            col=(max(d,d3)>0.0)?col:blue_fog;
            mix_col=smoothstep(AA,0.0,abs(d))+smoothstep(AA,0.0,abs(d2))+smoothstep(AA,0.0,abs(d3));
            col =  mix(col, vec3(0.0), mix_col);
        }
    }
    fragColor = vec4(col,1.0);
}
