// https://stackoverflow.com/a/4275343/4296866
float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

float sdf_rect(in vec2 uv, in vec2 position, in vec2 halfsize)
{
    vec2 componentWiseEdgeDistance = abs(uv - position) - halfsize;
    return min(max(componentWiseEdgeDistance.x, componentWiseEdgeDistance.y), 1.);
}

float sdf_rect_rounded(in vec2 uv, in vec2 position, in vec2 halfsize)
{
    vec2 componentWiseEdgeDistance = abs(uv - position) - halfsize;
    float outsideDistance = length(max(componentWiseEdgeDistance, 0.));
    float insideDistance = min(max(componentWiseEdgeDistance.x, componentWiseEdgeDistance.y), 0.);
    return outsideDistance + insideDistance;
}

struct rect {
    vec3 color;
    vec2 center;
    vec2 halfsize;
};

vec3 mixfn(in vec3 pixel, in vec3 color, in float mixer)
{
    vec3 cp = color; //color-pixel;
    return mix(pixel, cp, mixer);
}

float antialias(in float dist)
{
    return smoothstep(-1., 1., (dist-.04)/fwidth(dist));
}

void draw_rect(inout vec3 pixel, in vec2 position, rect r)
{
    float dist = sdf_rect_rounded(position, r.center, r.halfsize);
    float clampd = 1.-antialias(dist); // 1.-step(0.01, dist);
    pixel = mixfn(pixel, r.color, clampd);
}

