void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;
    p -= vec2(.5,.5);//move to center of screen

    float width_factor = 6.0;

    // Time varying pixel color
    vec3 back = 0.0 + .8 * cos(iTime+p.xyx+vec3(0,2,4));

    //back = vec3 (0.1, 0.2, 0.30);
    vec3 light = vec3 (0.0, 0.0,0.2);

    float hdist = width_factor * abs(p.y);

    //hdist -= smoothstep (.1, .6, width_factor * abs(p.y));

    light += vec3(hdist);

    // Output to screen
    fragColor = vec4(back,1.0);
    fragColor -= vec4(light,1.0);


}
