vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    // Time varying pixel color
    // vec2 dir = normalize(uv);
    // vec2 t2  = sin(iTime)*dir;
    // vec3 col = texture(iChannel0, uv+t2).rgb;
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // vec2 dir = normalize(uv);
    float d = length(uv)*50.;
    if(d >= 11.5 && d <= 50.5){
        float angle = atan(uv.y, uv.x);
        d += sin(16.*angle-10.*iTime);
    }
    if(d >= 11.5 && d <= 16.5) {
        float angle = atan(uv.y, uv.x);
        d += sin(8.*angle-10.*iTime);
    }
    if(d >= 30.5 && d <= 35.5) {
        float angle = atan(uv.y, uv.x);
        d += 10.*sin(8.*angle+5.*iTime);
    }
    if(d >= 37.5 && d <= 41.5) {
        float angle = atan(uv.y, uv.x);
        d += 10.*sin(16.*angle+2.5*iTime);
    }
    if(d >= 46.5 && d <= 50.5) {
        float angle = atan(uv.y, uv.x);
        d += 10.*sin(angle-iTime);
    }

    float g = sin(d);
    // vec3 col = vec3(g, g, g);
    // vec3                col = pal( g, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
    vec3 col = pal( g+.25*iTime, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.10,0.20) );

    // Output to screen
    fragColor = vec4(col,1.0);
}
