void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec3 uvw = vec3(gl_FragCoord.xyz) / iResolution.xyz;
uvw = uvw * 2.0 - 0.5;
float t = iTime * 0.1;
float wave = sin(uvw.x * 20.0 + t) * cos(uvw.y * 20.0 + t) * sin(uvw.z * 20.0 + t);
fragColor = vec4(cos(wave*4.), sin(wave), sin(2.*wave), 1.0);

}
