vec2 force(vec2 p, vec2 p1){
    vec2 to = p1 - p;
    float d = length(to) + .1;
    vec2 dir = to / d;
    float mag = (10. - (d / 10.)) / (d * d);

    return mag * dir;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec4 col = vec4(0.);


    if(iFrame == 0){
        col = vec4(hash3(uv), hash3(uv.yx), 0., 0.);
    }
    else{
        vec4 p;

        col = texelFetch(iChannel0, ivec2(fragCoord), 0);

        for(int i = 0; i < 100; i++){
            p = texelFetch(iChannel0, ivec2(i, 0), 0);
            col.zw += force(col.xy, p.xy)* iTimeDelta * .000001;
        }


        //col = texelFetch(iChannel0, ivec2(fragCoord), 0);
        //col.w += -.000981 * iTimeDelta;
        //col.w *= (col.y / abs(col.y)) * -((col.y - 1.) / abs(col.y - 1.));
        //col.z *= (col.x / abs(col.x)) * -((col.x - 1.) / abs(col.x - 1.));


        col.xy += col.zw;
        col.xy = clamp(col.xy, 0.001, 0.999);


    }

    fragColor = col;
}

float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx, vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0.);

    vec4 p;

    for(int i = 0; i < 100; i++){
        p = texelFetch(iChannel0, ivec2(i, 0), 0);
        col += .001 / (distance(uv, p.xy) + .001);
    }

    col = (col - .45) / abs(col - .45);

    fragColor = col;
}
