void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    uv *= 50.;

    float wave1 = sin(uv.x + iTime * 2.);
    float wave2 = cos(.5 * uv.y + iTime);
    float wave3 = sin(uv.y + iTime * 3.);
    float wave4 = cos(uv.x + iTime);
    float wave5 = sin(uv.x + iTime) * cos(uv.y + iTime);

    float something = uv.x;


    float finalValue = wave1 + wave2 + wave3 + wave4 + wave5 + something;

    vec4 finalWave = vec4(sin(finalValue), cos(finalValue), tan(finalValue), 1);

    finalWave *= 0.5 * sin(uv.x + iTime * 2.) + 0.75;

    fragColor = finalWave;
}
