{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0,uv);
}

//suppression

float Du = .2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    //init processing
    if(iFrame == 0){
        if(fragCoord.x > iResolution.x/2.-ORIGIN_SIZE && fragCoord.x < iResolution.x/2.+ORIGIN_SIZE &&
        fragCoord.y > iResolution.y/2.-ORIGIN_SIZE && fragCoord.y < iResolution.y/2.+ORIGIN_SIZE){
            fragColor.rgb = vec3(.5);
            fragColor.xyz += texture(iChannel2,uv).xyz*.1;
        }
        else{
            fragColor.rgb = vec3(1.);
        }
        fragColor.w = 1.;
    }
    else{
        vec3 laplacian = texture(iChannel0,(fragCoord+vec2(1.,0.))/iResolution.xy).rgb + 
                         texture(iChannel0,(fragCoord+vec2(-1.,0.))/iResolution.xy).rgb +
                         texture(iChannel0,(fragCoord+vec2(0.,1.))/iResolution.xy).rgb +
                         texture(iChannel0,(fragCoord+vec2(0.,-1.))/iResolution.xy).rgb -
                         texture(iChannel0,fragCoord/iResolution.xy).rgb*4.;

        vec3 u = texture(iChannel0,uv).rgb;
        vec3 v = texture(iChannel1,uv).rgb;

        vec3 du = Du*laplacian - u*v*v + f*(1.-u);

        fragColor = vec4(u + du*dt,1.);
    }
}

//excitement

float Dv = .1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    //init processing
    if(iFrame == 0){
        if(fragCoord.x > iResolution.x/2.-ORIGIN_SIZE && fragCoord.x < iResolution.x/2.+ORIGIN_SIZE &&
        fragCoord.y > iResolution.y/2.-ORIGIN_SIZE && fragCoord.y < iResolution.y/2.+ORIGIN_SIZE){
            fragColor.rgb = vec3(.25);
            fragColor.rgb += texture(iChannel2,uv).rgb*.1;
        }
        else{
            fragColor.xyz = vec3(0.);
        }
        fragColor.w = 1.;
    }
    else{
        vec3 laplacian = texture(iChannel1,(fragCoord+vec2(1.,0.))/iResolution.xy).rgb + 
                         texture(iChannel1,(fragCoord+vec2(-1.,0.))/iResolution.xy).rgb +
                         texture(iChannel1,(fragCoord+vec2(0.,1.))/iResolution.xy).rgb +
                         texture(iChannel1,(fragCoord+vec2(0.,-1.))/iResolution.xy).rgb -
                         texture(iChannel1,fragCoord/iResolution.xy).rgb*4.;

        vec3 u = texture(iChannel0,uv).rgb;
        vec3 v = texture(iChannel1,uv).rgb;

        vec3 dv = Dv*laplacian + u*v*v - (f+k)*v;

        fragColor = vec4(v + dv*dt,1.);
    }
}

#define ORIGIN_SIZE 50.

