float ToRad(float a){
    return a * (3.141592653589/180.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec3 col;
    for(float theta=0.;theta<ToRad(360.);theta+=ToRad(5.)){
        for(float phi=0.;phi<ToRad(180.);phi+=ToRad(5.)){
            vec3 a = .5  *vec3(sin(theta) * sin(phi),sin(theta) * cos(phi),cos(theta));
            float t = iTime;
            a.xz = vec2(cos(t) * a.x - sin(t) * a.z,sin(t) * a.x + cos(t) * a.z);
            a.yz = vec2(cos(t) * a.y - sin(t) * a.z,sin(t) * a.y + cos(t) * a.z);


            if(length(uv - a.xy)<.01){
              if(a.z < 0.){
                  col.r = 100. * -(length(uv - a.xy)-0.01);

              }
            }


        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
