//A simple Gaussian blur sampling 8 points
uniform sampler2D NAME;

#define E 2.7182818
#define PI 3.141592565
float Gauss(float x,float y,float _x)
{
   float s = (1.0);//Blur Power
   return pow(E,-((x*x)+(y*y))/(2.0*s*s))/(2.0*PI*s*s);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   vec2 uv = fragCoord/iResolution.xy;
   // uv.x *= iResolution.x/iResolution.y;
   vec4 matVec;
   vec4 col;
   float tot;

   float _x = 0.005;

   matVec = vec4(Gauss(-0.01,0.01,_x),Gauss(0.0,0.01,_x),Gauss(-0.01,0.00,_x),Gauss(0.00,0.00,_x));
   tot = matVec.r*4. + matVec.g + matVec.b*2. + matVec.a;


   //row1 of col Mat
   vec4 col11 = texture(iChannel0,clamp(uv - vec2(-_x,_x),vec2(0.),vec2(1.)));
   vec4 col12 = texture(iChannel0,clamp(uv - vec2(0.0 ,_x),vec2(0.),vec2(1.)));
   vec4 col13 = texture(iChannel0,clamp(uv - vec2(_x ,_x),vec2(0.),vec2(1.)));


   //row2 of col Mat
   vec4 col21 = texture(iChannel0,clamp(uv - vec2(_x, _x),vec2(0.),vec2(1.)));
   vec4 col22 = texture(iChannel0,clamp(uv - vec2(0.0 , 0.0),vec2(0.),vec2(1.)));
   vec4 col23 = texture(iChannel0,clamp(uv - vec2(-_x , _x),vec2(0.),vec2(1.)));


   //row3 of col Mat
   vec4 col31 = texture(iChannel0,clamp(uv - vec2(_x,-_x),vec2(0.),vec2(1.)));
   vec4 col32 = texture(iChannel0,clamp(uv - vec2(0.0 ,-_x),vec2(0.),vec2(1.)));
   vec4 col33 = texture(iChannel0,clamp(uv - vec2(-_x ,-_x),vec2(0.),vec2(1.)));

   col += col11 * matVec.r;
   col += col12 * matVec.g;
   col += col13 * matVec.r;


   col += col21 * matVec.b;
   col += col22 * matVec.a;
   col += col23 * matVec.b;



   col += col31 * matVec.r;
   col += col32 * matVec.g;
   col += col33 * matVec.r;

   col /= tot;
   fragColor = col;
}
