float Vignette(vec2 uv) {
    return 0.5 * length((2. * uv - iResolution.xy) / iResolution.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
	vec2 q = (3.0*fragCoord-iResolution.xy) / iResolution.y;
    vec2 p = q;

    float time = (300. + iTime * 0.2 + iMouse.x)*0.02;

	p += 6.*cos( .2*p.yx + time);

	float r = length( p );

    vec3 col1 = texture( iChannel0, vec2(r,0.0), 0.0 ).zyx;
    vec3 col2 = texture( iChannel0, vec2(r+0.04,0.0), 0.0 ).zyx;

    vec3 col = col1;
    col += 0.1;
    col *= 1.0 + 0.4*sin(r+vec3(0.0,3.0,3.0));
    col -= 4.0*max(vec3(0.0),col1-col2).x;
    col += 1.0*max(vec3(0.0),-col1).x - 0.1;
    col *= -1.7 - Vignette(uv);

    fragColor = vec4(col, 1.);
    //fragColor = vec4(Vignette(uv));
}
