// #define circular 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Convert to circular coordinates
#ifdef circular
    uv -= 0.5;
    float cd = length(uv);
    uv.x = mod(1.0+iTime*-0.1+atan(uv.x,uv.y)/3.14*2.5,1.0);
    uv.y = max(cd-0.06,0.0)*2.3;
#endif
    vec2 res = iChannelResolution[0].xy / 7.0;
    vec2 pt = mod(uv, 1.0/res);
    uv -= pt;
    pt *= res;

    // Remove green
    vec4 tex = texture(iChannel0,uv);
    tex.g *= 0.93;
    float newG = min(tex.g,max(tex.r,tex.b));
    tex.g = newG;

    // distance from dot center
    float x = (0.7071-length(pt -0.5))/0.7071;
    // inverted black and white pixel (unweighted for rgb but i'm colorblind anyway :) )
    float y = 1.0-pow(max(0.0,length(tex.rgb)-0.2) / 1.732, 1.0/2.2);

    // max out saturation
    vec3 clr = tex.rgb / max(max(tex.r,tex.g),tex.b);
    // Quantize to 8 colors
    clr = round(clr*2.0)/2.0;


	fragColor = vec4(clr * vec3(smoothstep(y,y+0.2,x)),1.0);
}
