// fixed by FabriceNeyret2

float f2( float n ){
    float v = fract(n);
    n = 2.*fract(v*2.+.5);
    n = n < 1. ? n : 2.-n;
    n = n*n*.5;
    return v > .5 ? n : 1.-n;
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/R;

    O = mix( vec4(0,1.-U.y,1,1),  
             vec4(1,   U.y,0,1), 
             smoothstep( 0., 1.5/R.y, f2(U.x+iTime*.2) - U.y ) 
           );
}
