const int num_samples = 128;
const float attenuation = 0.95;

vec3 radial_blur_filter(in vec2 origin, in vec2 point)
{
    vec3 color = vec3(0.0);
    vec2 delta = (origin - point) / float(num_samples);
    vec2 p = point;

    for(int s = 0; s < num_samples; s++)
    {
        float weight = pow(attenuation, float(s));
        color += weight * texture(iChannel0, p).rgb;
    	p += delta;
    }

	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord / iResolution.xy;

    vec2 mouse = vec2(0.5+0.3*sin(iTime/5.0),1.0);
	vec3 blur = radial_blur_filter(mouse, p);

    vec3 color = texture(iChannel0, p).rgb/11.0;

    color *= blur;

    fragColor = vec4(color, 1.0);
}
