mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

float qq(vec2 uv){
    uvec2 uuv = uvec2(uv*256.);
    return float(uuv.x&uuv.y)/128.;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy-.5* iResolution.xy)/iResolution.y;
    uv*=4.;


    uv*=rot(asin(sin(iTime+length(uv)))*.5);
    uv = vec2(log(length(uv)),atan(uv.y,uv.x)*3.14*.5);
    uv.x -=iTime;
    float sc= 1.;
    for(float i=0.;i++<4.;){
        uv =  asin(sin(abs(uv)-.75));
        uv *= rot(.785*i);
        uv*=1.2;
        sc*=1.2;
    }
    uv/=sc;
    float d = abs(uv.x)-.1-clamp(.1,.15,uv.y)+qq(uv)*.2;
    d = min(d,abs(uv.y)-.1-qq(uv)*.2);
    d = smoothstep(fwidth(d),0.,d);
    vec3 col = vec3(1.)*d;
    col = mix(vec3(.8+qq(uv)),vec3(.5-qq(uv),.0,.0),col);
    fragColor = vec4(sqrt(col),1.0);
}
