void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 colIn = texture(iChannel0, fragCoord/iResolution.xy);
    vec2 uv = (fragCoord/iResolution.xy)-vec2(0.5);
    uv.x /= iResolution.y/iResolution.x;


    /*
    uv*=0.2;
    uv.x -= 0.3;
    uv.y -= 0.7;
    //*/

    //*
    //uv*=5e-6;//this value will take you to the limit of the floating point resolution
    uv*=5e-2;
    uv.x -= 0.230602;
    uv.y -= 0.700407;
    //*/



    vec2 coords;
    bool done = colIn.z != 0.0;
    bool prevDone = done;
    float margin = 10.0;

    if (!done) {
        coords = colIn.xy;

        vec2 i = coords;
        vec2 o;
        o.x = i.x*i.x - i.y * i.y;
        o.y = i.x * i.y * 2.0;

        coords = o + uv;


        if (length(coords) > margin)
        {
            done = true;
        }
    }

    fragColor = vec4(coords, done?(prevDone?colIn.z:texture(iChannel1, vec2(0.5)).x):0.0 ,1.0);

    if (texture(iChannel1, vec2(0.5)).x < 0.1)
    {
        fragColor.xy = uv;
        fragColor.zw = vec2(0.0);
    }

}
