#define R iResolution.xy

vec3 checker(vec2 uv, float tile) {
    vec2 id = floor(uv*tile);
    return mix(
        vec3(223.0, 230.0, 233.0) / 255.0,
        vec3(45.0, 52.0, 54.0) / 255.0,
        float(mod(id.x-id.y, 2.0) > 0.0)
    );
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = fc / R.y;
    float c = R.x*0.5;
    col = checker(uv, fc.x <= c ? 32.0 : 64.0); // checker texture
    float thick = iResolution.x/96.0;
    col = mix(col, vec3(0.0, 0.0, 0.0), float(fc.x <= c+thick && fc.x >= c-thick)); // separator line
    O = vec4(col, 1.0);
}
