vec2 glitch(vec2 uv) {
    float glitchX = (sin(uv.y * 10.0 + iTime * 2.0) * 0.05) + (sin(uv.x * 10.0 + iTime * 2.0) * 0.05);
    float glitchY = (cos(uv.y * 10.0 + iTime * 2.0) * 0.05) + (cos(uv.x * 10.0 + iTime * 2.0) * 0.05);
    return vec2(uv.x + glitchX, uv.y + glitchY);
}

vec4 retroTape(vec2 uv) {
    vec4 color = texture(iChannel0, uv);
    vec4 glow = texture(iChannel0, uv + vec2(0.5, 0.5));
    color.rgb += glow.rgb * 0.5;
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = gl_FragCoord.xy / vec2(1280.0, 720.0);
    vec2 glitchUV = glitch(uv);
    vec4 finalColor = retroTape(glitchUV);
    fragColor = finalColor;
}
