void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv * 4.0 - 2.0;
    float s = sin(iTime);
    float c = cos(iTime);
    p = vec2(dot(p, vec2(c, s)), dot(p, vec2(-s, c)));
    float r = dot(p,p);
    float f;
    f  = 0.50000*sin(r*12.9898+dot(p,vec2(78.233,31.646)));
    f += 0.25000*sin(r*12.9898+dot(p,vec2(31.646,78.233)));
    f += 0.12500*sin(r*12.9898+dot(p,vec2(31.646,31.646)));
    fragColor = vec4(f * vec3(0.5, 1.0, 1.0), 1.0);

}
