void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float distancia = sin(iTime)*.5+.5;
    float fuerza = 0.05; 
    vec2 uvR=uv+smoothstep(0.,distancia,distance(uv,vec2(0.5)))*fuerza;
    vec2 uvG=uv;
    vec2 uvB=uv-smoothstep(0.,distancia,distance(uv,vec2(0.5)))*fuerza;

    float r = texture(iChannel0,uvR).r;
    float g = texture(iChannel0,uvG).g;
    float b = texture(iChannel0,uvB).b;

    vec3 col = vec3(r,g,b);
 // col.rgb=vec3(pow(smoothstep(0.,distancia,distance(uv,vec2(0.5))),));
    // Output to screen
    fragColor = vec4(col,1.0);
}
