void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iMouse.z > .5)
        fragCoord -= iMouse.xy * 2.;
    else
        fragCoord.x += iTime * iResolution.x / 30.;

    vec2 uv = fragCoord / iResolution.y;

    float m = smoothstep(.2, .4, textureLod(iChannel0, uv * 2., 0.).w + .05);

    vec3 bg = texture(iChannel1, uv).rgb * 1.6;
    vec3 col = vec3(.1, .21, .1) * bg * .9;

    uv += textureLod(iChannel0, uv, 0.).zw * .007;
    uv *= 8.;

    vec2 f = fract(uv), c = floor(uv);

    if(mod(floor(c.x), 2.) > .5)
        f.x = 1. - f.x;
    if(mod(floor(c.y), 2.) > .5)
        f.y = 1. - f.y;

    float w = .26, v = .16, d;
    vec2 r = texelFetch(iChannel0, ivec2(c) & 255, 0).xy;

    if(r.x < .8)
    {
        float a = abs(max(f.x, f.y) - .5 - w), b = abs(.5 - min(f.x, f.y) - w);

        if(r.y < .5)
            d = min(a, max(v - a + w / 2., b));
        else
            d = min(b, max(v - b + w / 2., a));
    }
    else
    {
        f.x = 1. - f.x;
        d = min(abs(max(f.x, f.y) - .5 + w), abs(.5 - min(f.x, f.y) + w));        
    }

    d -= w / 3.;

    float h = length(fwidth(uv)) / 2.;
    col = mix(col, bg * (vec3(1, 1, .5) + .3), (1. - smoothstep(-h, +h, d)) * m);

    fragColor = vec4(sqrt(col), 1.);
}
