void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    float r = .2; // * (sin(iTime) * .5 + .5);
    float w = .1 * r / .1;

    vec3 col = vec3(smoothstep(.002, -.002, abs(length(uv) - r) - w));

    col *= smoothstep(.2, -.2, sin((atan(uv.x, uv.y) + length(uv) * 8.) * 7. - iTime * 10.));

    col *= smoothstep(.2, -.2, abs(fract(length(uv*30.) - iTime) * 2. - 1.) - .5);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
